// ======== FUNCTIONS ======== //
int stationOrderIdx(const Station& s) {
    if (!validStation(s)) {
        throw std::invalid_argument("Invalid station s");
    }

    for (int i = 0; i < STATION_ORDER.size(); ++i) {
        if (sameStation(STATION_ORDER[i], s)) {
            return i;
        }
    }

    return -1;
}

int travelPrice(const Station& s1, const Station& s2, const TicketType& tt) {
    int idx1 = stationOrderIdx(s1);
    int idx2 = stationOrderIdx(s2);

    if (idx1 < idx2) {
        int temp = idx1;
        idx1 = idx2;
        idx2 = temp;
    }

    // Now idx1 >= idx2
    int price = PRICE_TABLE.at(idx1)[idx2];

    if (tt == ADULT) {
        return price;
    } else if (tt == CHILD) {
        return ADULT_TO_CHILD_PRICE.at(price);
    } else if (tt == ELDERLY) {
        return ADULT_TO_ELDERLY_PRICE.at(price);
    }
}